import React, { useContext, useEffect, useState } from 'react';
import axios from "axios";
import { UserContext } from '../../App';
const Home=()=>{
    const[data,setData]=useState([]);
    const {state,dispatch}=useContext(UserContext);

    //functionality to fetch the data 
    useEffect(()=>{
        //Fetch the data from post db 
        axios.get("http://localhost:5000/allpost",{
            headers:{
                "Content-Type":"Application/json",
                "Authorization":"Bearer "+ localStorage.getItem("jwt")
            }
        }).then(response=>{
            console.log(response.data);
            setData(response.data.posts);
        }).catch(error=>{
            console.log(error);
        })
    },[])

//Define a functionality to fetch the like on frontend 
const likePost=(id)=>{
    //use axios for put http 
    axios.put("http://localhost:5000/like",
    {
    postId:id //include the postid of the user logged in 
    },
    {
        headers:{
            "Content-Type":"application/json",
            "Authorization":"Bearer "+ localStorage.getItem("jwt")
        }
    }
    ).then(response=>response.data)
    .then(result=>{
        console.log(result);
        const newData=data.map(item=>{
            if(item._id===result._id){
                return result;//user will be like post for once only 
            }else{
                return item; //keep other post unchanged
            }
        });
        setData(newData);//update data after like 
    }).catch(error=>{
        console.log(error);
    })
}


 //Define a functionality to fetch the unlike on frontend 
const unlikePost=(id)=>{
    //apply a logic to update the unlike endpoint
    axios.put("http://localhost:5000/unlike",
    {
        postId:id
    },
    {
        headers:{
            "Content-Type":"application/json",
            "Authorization":"Bearer "+localStorage.getItem("jwt")
        }
    } 
    ).then(response=>response.data)
    .then(result=>{
        console.log(result);
        const newData=data.map(item=>{
            if(item._id===result._id){
                return result;
            }else{
                return item;
            }
        });
        setData(newData);
    }).catch(error=>{
        console.log(error);
    })
}



    return(
        <div className='home'>
            {
                data.map(item=>{
                    return(
                        <div className='card home-card' key={item._id}>
                        <h5>{item.postedBy.name}</h5>
                        <div className='card-image'>
                        <img className='post' src={item.photo} alt="image1"/>
                        </div>

                        <div className='card-content'>
                        <i className="material-icons" style={{color:"red",fontSize: "30px"}}>favorite</i>

                        {/* check if the current user has like post or different user  */}
                        {item.likes.includes(state._id)
                        ?      
                        /* //display unlike button if liked      */
                        <button className="material-icons" onClick={()=>{unlikePost(item._id)}}>
                        thumb_down
                        </button>
                        :
                        /* //display like button if not liked  */
                        <button className="material-icons" onClick={()=>{likePost(item._id)}}>
                        thumb_up
                        </button>
                        }
                        <h6>Likes:{item.likes.length}</h6>
                         <h6>{item.title}</h6>
                         <p>{item.body}</p>
                         <input  type="text" placeholder='Add Comment'/>
                        </div>
                    </div>
                    )
                })
            }

        </div>
    )
}

export default Home;
