import React, { useEffect, useState } from "react";
import "../../App.css";
import { useNavigate } from "react-router-dom";
import axios from "axios";
const CreatePost=()=>{
//create 3 states for title,body,image upload
const[title,setTitle]=useState("");
const[body,setBody]=useState("");
const[image,setImage]=useState(null);
const[url,setUrl]=useState("");
//React router hook for navigation



    return(
       <div className="card input-filed postcontainer">
        <input
        type="text"
        placeholder="Title..."
        value={title}
        onChange={(e)=>setTitle(e.target.value)}
        />
        <input
        type="text"
        placeholder="Add your content Here."
        value={body}
        onChange={(e)=>setBody(e.target.value)}
        />
    <div className="file-field input-field">
      <div className="btn #3d5afe indigo accent-3">
        <span>Cancel Order/Edit Order</span>
        <input type="file" 
        onChange={(e)=>setImage(e.target.files[0])}
        />
      </div>
      <div className="file-path-wrapper">
        <input className="file-path validate" type="text"/>
      </div>
    </div>
    {/* //display the uploaded image if the url is available  */}
    {url && (
      <img
       src={url}
       alt="Uploaded"
       style={{
        width:"100%",
        maxHeight:"300px",
        objectFit:'cover'
       }}
      />
    )}


 </div>
    );
};

export default CreatePost;
